import os
from io import BytesIO
from tkinter import *
from tkinter import filedialog, messagebox, ttk
from tkinter.colorchooser import askcolor
from tkinter.font import Font
from openpyxl import load_workbook
from reportlab.pdfgen import canvas
from PyPDF4 import PdfFileReader, PdfFileWriter
from PIL import ImageTk, Image
import base64

class App:
    def __init__(self, master: Tk):
        self.master = master
        self.peoples = []
        self.files = []
        self.processed_files_count = 0

        icon_base64 = """"""
        self.icon = ImageTk.PhotoImage(Image.open(BytesIO(base64.b64decode(icon_base64))))

        self.master.iconphoto(True, self.icon)

        self.master.title('Filigraneur de fichiers PDF')
        self.master.resizable(False, False)

        # UI Elements
        self.label = Label(master, text='Liste des participants')
        self.label_files = Label(master, text='Liste des fichiers')

        self.listbox = Listbox(master, height=6, width=30, selectmode=EXTENDED)
        self.files_listbox = Listbox(master, height=6, width=30, selectmode=EXTENDED)

        self.label_font_size = Label(master, text='Taille de la police :')
        self.label_repetitions = Label(master, text='Répétitions par page :')

        self.spin_font_size = Spinbox(master, from_=1, to=100, textvariable=IntVar(value=12))
        self.spin_repetitions = Spinbox(master, from_=1, to=20, textvariable=IntVar(value=1))
        self.entry_color = Entry(master, textvariable=StringVar(value='#a0a0a0'))
        self.color_button = Button(master, text='Choisir une couleur', command=self.choose_color)

        self.load_people_btn = Button(master, text='Charger participants...', command=self.load_people)
        self.load_files_btn = Button(master, text='Charger fichiers...', command=self.load_files)
        self.add_people_btn = Button(master, text='Ajouter un participant', command=self.add_people)
        self.delete_people_btn = Button(master, text='Supprimer participants', command=self.delete_people)
        self.delete_file_btn = Button(master, text='Supprimer fichier', command=self.delete_file)
        self.add_watermark_btn = Button(master, text='Filigraner mes fichiers', command=self.add_watermark_to_all_participants)
        self.add_watermark_btn.config(font=Font(size=12, weight='bold'))

        self.progress_var = DoubleVar()
        self.progress_bar = ttk.Progressbar(master, variable=self.progress_var, maximum=100)

        self.select_output_folder_btn = Button(master, text='Sélectionner le dossier de sortie...', command=self.select_output_folder)
        self.output_folder_label = Label(master, textvariable=StringVar(value="Aucun dossier sélectionné"))

        # Layout
        self.label.grid(row=0, column=0, columnspan=1, padx=10, sticky='w')
        self.label_files.grid(row=0, column=1, columnspan=1, padx=10, sticky='w')
        self.listbox.grid(row=1, column=0, columnspan=1, padx=10, pady=5, sticky='nsew')
        self.files_listbox.grid(row=1, column=1, columnspan=1, padx=10, pady=5, sticky='nsew')

        self.label_font_size.grid(row=9, column=0, padx=10, pady=5, sticky='w')
        self.label_repetitions.grid(row=10, column=0, padx=10, pady=5, sticky='w')
        self.color_button.grid(row=11, column=0, padx=10, pady=5, sticky='we')

        self.spin_font_size.grid(row=9, column=1, columnspan=1, padx=10, pady=5, sticky='we')
        self.spin_repetitions.grid(row=10, column=1, columnspan=1, padx=10, pady=5, sticky='we')
        self.entry_color.grid(row=11, column=1, columnspan=1, padx=10, pady=5, sticky='we')

        self.select_output_folder_btn.grid(row=12, column=0, columnspan=1, padx=10, pady=5, sticky='we')
        self.output_folder_label.grid(row=12, column=1, columnspan=1, padx=10, pady=5, sticky='w')

        self.load_people_btn.grid(row=2, column=0, columnspan=1, sticky='ew', padx=10, pady=5)
        self.load_files_btn.grid(row=2, column=1, columnspan=1, rowspan=2, sticky='nsew', padx=10, pady=5)
        self.add_people_btn.grid(row=3, column=0, columnspan=1, sticky='ew', padx=10, pady=5)
        self.delete_people_btn.grid(row=4, column=0, columnspan=1, sticky='ew', padx=10, pady=5)
        self.delete_file_btn.grid(row=4, column=1, columnspan=1, sticky='ew', padx=10, pady=5)
        self.add_watermark_btn.grid(row=5, column=0, columnspan=2, rowspan=2, sticky='nsew', padx=10, pady=10)

        master.mainloop()

    def load_people(self):
        file_path = filedialog.askopenfilename(initialdir=os.getcwd(), title="Sélectionnez le fichier Excel", filetypes=[("Fichier Excel", "*.xlsx *.xls")])

        wb = load_workbook(file_path)
        sheet = wb.active

        people_list = []

        for row in sheet.iter_rows():
            full_name = f"{row[0].value} {row[1].value} {row[2].value}"
            people_list.append(full_name)

        self.peoples = tuple(people_list)

        wb.close()

        self.listbox.delete(0, END)
        self.listbox.insert(END, *self.peoples)

    def load_files(self):
        file_paths = filedialog.askopenfilenames(initialdir=os.getcwd(), title="Sélectionnez les fichiers PDF", filetypes=[("Fichiers PDF", "*.pdf")])

        file_list = list(self.files)

        for file_path in file_paths:
            file_name = file_path
            if file_name not in file_list:
                file_list.append(file_name)

        self.files = tuple(file_list)

        self.files_listbox.delete(0, END)
        self.files_listbox.insert(END, *self.files)

    def add_people(self):
        add_people_window = Toplevel()
        add_people_window.title('Ajouter un participant')

        label_civility = Label(add_people_window, text='Civilité')
        label_firstname = Label(add_people_window, text='Prénom')
        label_lastname = Label(add_people_window, text='Nom')

        entry_civility = Entry(add_people_window)
        entry_firstname = Entry(add_people_window)
        entry_lastname = Entry(add_people_window)

        def add_people_to_list():
            civility = entry_civility.get()
            firstname = entry_firstname.get()
            lastname = entry_lastname.get()

            if civility and firstname and lastname:
                full_name = f"{civility} {firstname} {lastname}"
                self.peoples += (full_name,)
                self.listbox.insert(END, full_name)
                add_people_window.destroy()
            else:
                messagebox.showerror('Erreur', 'Veuillez remplir tous les champs')

        add_btn = Button(add_people_window, text='Ajouter', command=add_people_to_list)

        label_civility.grid(row=0, column=0, sticky='w')
        label_firstname.grid(row=1, column=0, sticky='w')
        label_lastname.grid(row=2, column=0, sticky='w')

        entry_civility.grid(row=0, column=1)
        entry_firstname.grid(row=1, column=1)
        entry_lastname.grid(row=2, column=1)

        add_btn.grid(row=3, column=0, columnspan=2)

    def delete_people(self):
        selection = self.listbox.curselection()
        for i in selection[::-1]:
            self.listbox.delete(i)
        self.peoples = tuple(self.listbox.get(0, END))

    def delete_file(self):
        selection = self.files_listbox.curselection()
        for i in selection[::-1]:
            self.files_listbox.delete(i)
        self.files = tuple(self.files_listbox.get(0, END))

    def choose_color(self):
        color = askcolor(title="Sélectionnez une couleur")
        if color[1]:
            self.entry_color.delete(0, END)
            self.entry_color.insert(0, color[1])

    def update_progress(self, current, total):
        percentage = (current / total) * 100
        self.progress_var.set(percentage)
        self.master.update_idletasks()
    
    def validate_fields(self):
        if not self.peoples:
            messagebox.showerror('Erreur', 'Veuillez charger la liste des participants.')
            return False

        if not self.files:
            messagebox.showerror('Erreur', 'Veuillez charger la liste des fichiers.')
            return False

        try:
            font_size = float(self.spin_font_size.get())
            if font_size <= 0:
                messagebox.showerror('Erreur', 'La taille de la police doit être un nombre positif.')
                return False
        except ValueError:
            messagebox.showerror('Erreur', 'La taille de la police doit être un nombre.')
            return False

        try:
            repetitions = int(self.spin_repetitions.get())
            if repetitions <= 0:
                messagebox.showerror('Erreur', 'Le nombre de répétitions doit être un entier positif.')
                return False
        except ValueError:
            messagebox.showerror('Erreur', 'Le nombre de répétitions doit être un entier.')
            return False

        color = self.entry_color.get()
        if not color.startswith('#') or not all(c in '0123456789abcdefABCDEF' for c in color[1:]):
            messagebox.showerror('Erreur', 'Veuillez sélectionner une couleur valide.')
            return False

        # Check if an output folder is selected and if it exists
        if not os.path.isdir(self.output_folder_label.cget('text')):
            messagebox.showerror('Erreur', 'Veuillez sélectionner un dossier de sortie.')
            return False

        return True


    def add_watermark_to_pdf(self, pdf_reader, watermark_text, font_size, repetitions, color):
        pdf_writer = PdfFileWriter()

        for page_num in range(pdf_reader.getNumPages()):
            page = pdf_reader.getPage(page_num)
            width, height = map(float, page.mediaBox.upperRight)

            packet = BytesIO()
            can = canvas.Canvas(packet, pagesize=page.mediaBox)

            text_width = float(can.stringWidth(watermark_text, "Helvetica", font_size))
            text_height = float(font_size)

            horizontal_spacing = (width - text_width) / (repetitions + 1)
            vertical_spacing = (height - text_height) / (repetitions + 1)

            for i in range(repetitions):
                x = (i + 1) * horizontal_spacing
                y = (i + 1) * vertical_spacing

                can.setFont("Helvetica", font_size)
                can.setFillColor(color)
                can.drawString(x, y, watermark_text)

            can.save()

            packet.seek(0)
            new_pdf = PdfFileReader(packet)
            page.mergePage(new_pdf.getPage(0))
            pdf_writer.addPage(page)

        return pdf_writer

    def select_output_folder(self):
        output_folder = filedialog.askdirectory(initialdir=os.getcwd(), title="Sélectionnez le dossier de sortie")
        self.output_folder_label.config(textvariable=StringVar(value=output_folder))

    def create_participant_folders(self):
        output_folder = self.output_folder_label.cget("text")
        for participant in self.peoples:
            folder_name = os.path.join(output_folder, participant)
            os.makedirs(folder_name, exist_ok=True)

    def add_watermark_to_participant_pdfs(self, participant, font_size, repetitions, color):
        watermark_text = f"{participant}"

        total_files = len(self.files)
        for _, file_name in enumerate(self.files):
            with open(file_name, "rb") as pdf_file:
                pdf_reader = PdfFileReader(pdf_file)
                pdf_writer = self.add_watermark_to_pdf(pdf_reader, watermark_text, font_size, repetitions, color)

                output_file_name = f"{self.output_folder_label.cget('text')}/{participant}/{os.path.basename(file_name)}"

                with open(output_file_name, "wb") as output_file:
                    pdf_writer.write(output_file)

            self.processed_files_count += 1
            self.update_progress(self.processed_files_count, total_files * len(self.peoples))

    def add_watermark_to_all_participants(self):
        if not self.validate_fields():
            return
        font_size = float(self.spin_font_size.get())
        repetitions = int(self.spin_repetitions.get())
        color = self.entry_color.get()

        self.add_watermark_btn.grid_forget()
        self.progress_bar.grid(row=5, column=0, columnspan=2, rowspan=2, sticky='nsew', padx=10, pady=10)
        self.master.update_idletasks()
        self.master.update()
        self.create_participant_folders()
        for participant in self.peoples:
            self.add_watermark_to_participant_pdfs(participant, font_size, repetitions, color)
        if self.processed_files_count == len(self.files) * len(self.peoples):
            messagebox.showinfo("Succès", "Tous les fichiers ont été filigranés avec succès!")
            self.progress_var.set(0)
            self.processed_files_count = 0
            self.progress_bar.grid_forget()
            self.add_watermark_btn.grid(row=5, column=0, columnspan=2, rowspan=2, sticky='nsew', padx=10, pady=10)

app = App(Tk())